// Grab the control
var dateSource = $("#P18_C78_");
// Grab the value of the control
var dateValue = $.datepicker.parseDate("dd/mm/yy", dateSource.val());
// Reformat the date in the desired format
var newDate = $.datepicker.formatDate("DD, d MM, yy", dateValue);
// Grab the destination control
var dateDestination = $("#P18_C79_");
// Asign it's value
dateDestination.val(newDate);

// Grab the value of our input
var input = $("#P18_C84_").val();
// Grab the destination control
var dropdown = $("#P18_C83_");
// Set the input into our dropdown
dropdown.append($("<option></option>").text(input));
