var actionsTable = $("#rapid_P0_C288");
actionsTable.find("tr:not(:nth(0))").each( function() {
  var actionRow = $(this);
  var actionType = actionRow.find("td:nth(0)").html();
  var actionCheckbox = actionRow.find("input");
  actionCheckbox.prop("checked", false);
  for (var i in actionTypesData) {
    if (actionType == actionTypesData[i]) {
      actionCheckbox.prop("checked",true);
      break;
    }
  }
});
