if (!value) return "An application name must be provided";
if (!value.match(new RegExp("^[0-9A-Za-z\._-]+$"))) return "An application name can only contain characters a-z, A-Z, 0-9, ., -, and _";
if (value.toLowerCase() != $("#rapid_P0_C43").val().toLowerCase()) {
  var exists = false;
  $.ajax({
    url: "designer?action=checkApp&name=" + value,
    type: "GET",    
    async: false,                
    success: function(result) {
      if (result) {
        exists = true;
      }
    }
  });
  if (exists) return "An application with this name already exists";
}

if (!value) return "An application version must be provided";
if (!value.match(new RegExp("^[0-9A-Za-z\._-]+$"))) return "An application version can only contain characters a-z, A-Z, 0-9, ., -, and _";
if (value.toLowerCase() != $("#rapid_P0_C1044_").val().toLowerCase()) {
  var exists = false;
  var name = $("#rapid_P0_C392_").val();
  $.ajax({
    url: "designer?action=checkVersion&name=" + name + "&version=" + value,
    type: "GET",    
    async: false,                
    success: function(result) {
      if (result) {
        exists = true;
      }
    }
  });
  if (exists) return "This version of the application already exists";
}

