// fetch the user data
var data = getData_dataStore(ev, 'rapid_P0_C243', 'user', {storageType:"S"});
if (data && data.rows && data.fields) {
  var rolesIndex = 0;
  var currentUserIndex = 0;
  // find the roles field and currentUser indexes
  for (var i in data.fields) {
    if (data.fields[i] == "roles") rolesIndex = i;
    if (data.fields[i] == "currentUser") currentUserIndex = i;    
  }
  var roles = data.rows[0][rolesIndex];
  var useAdmin = false;
  var useDesign = false;
  for (var i in roles) {
    if (roles[i] == "RapidAdmin") useAdmin = true;
    if (roles[i] == "RapidDesign") useDesign = true;
  }
  $("#rapid_P0_C879_").prop("checked", useAdmin);
  $("#rapid_P0_C880_").prop("checked", useDesign);
  $("#rapid_P0_C879_").prop("disabled", data.rows[0][currentUserIndex]);
}

