validation.javaScript:
var name = $("#rapid_P3_C17").val();
if (!name) return "A page name must be provided";
if (!name.match(new RegExp("^[0-9A-Za-z_-]+$"))) return "A page name can only contain characters a-z, A-Z, 0-9, -, and _";
var exists = false;
$.ajax({
    url: "designer?a=" + _app.id + "&action=checkPage&name=" + name,
    type: "GET",    
    async: false,                
    success: function(result) {
        if (result) {
            exists = true;
        }
    }
});
if (exists) return "A page with this name already exists";
