control.getDataFunction:

var dataStore;
switch (details.storageType) {
	case "L":
		// use localStorage
		dataStore = localStorage;
	break;
	case "S": 
		// use sessionStorage
		dataStore = sessionStorage;
	break;
	case "P": 
		// instansiate an object in the page if there isn't one
		if (!window[details.id + "datastore"]) window[details.id + "datastore"] = {};
		// use the in page object
		dataStore = window[details.id + "datastore"];
	break;
}  
if (dataStore) {
	if (details.id) id = details.id;
	var data = null;
	var dataString = dataStore[id];
	if (dataString) {
		var data = JSON.parse(dataString);
		if (data) {		
			if (data.rows && data.fields) {
				if (data.rows[0] && field) {
					for (var i in data.fields) {
						if (data.fields[i].toLowerCase() == field.toLowerCase()) {
							return data.rows[0][i];
						}
					}
				} else {
					return data;
				}
			} else if (field && data[field]) {
				return data[field];
			}
		}	 
	} 
	return data;		
}

control.setDataJavaScript:

var dataStore;
switch (details.storageType) {
	case "L":
		// use localStorage
		dataStore = localStorage;
	break;
	case "S": 
		// use sessionStorage
		dataStore = sessionStorage;
	break;	
	case "P": 
		// instansiate an object in the page if there isn't one
		if (!window[details.id + "datastore"]) window[details.id + "datastore"] = {};
		// use the in page object
		dataStore = window[details.id + "datastore"];
	break;
} 	   
if (dataStore) {
	if (details.id) id = details.id;
	if (data != null && data !== undefined) {
		data = makeDataObject(data, field);
		if (details.merge && dataStore[id]) data = mergeDataObjects(data, JSON.parse(dataStore[id]));		
		dataStore[id] = JSON.stringify(data);
	} else {
		dataStore[id] = null;
	}
}
