control.getHtmlFunction:

// return our html for adding to our parent object	
var value = "";
if (this.value) value = this.value  
switch (this.controlType) {
	case "N" : return "<input id='" + this.id + "' value='" + value + "' type='text'" + (this.maxLength ? " maxlength='" + this.maxLength + "'" : "") + " class='input" + getStyleClasses(this) + "'></input>";
	case "L" : return "<textarea id='" + this.id + "' class='input" + getStyleClasses(this) + (this.autoHeight ? " autoheight" : "")  + "' " + (this.maxLength ? " maxlength='" + this.maxLength + "'" : "") + ">" + value + "</textarea>";
	case "P" : return "<input id='" + this.id + "' value='" + value + "' type='password'" + (this.maxLength ? " maxlength='" + this.maxLength + "'" : "") + " class='input" + getStyleClasses(this) + "'></input>";
	case "R" : return "<input id='" + this.id + "' value='" + value + "' type='text'" + (this.maxLength ? " maxlength='" + this.maxLength + "'" : "") + " class='input" + getStyleClasses(this) + "' readonly='readonly'></input>";
}

control.setDataJavaScript:

var control = $("#" + id);
if (data != null && data !== undefined) {	
	data = makeDataObject(data, field);
	if (data.rows && data.rows[0]) {	        		
		if (field && data.fields && data.fields.length > 0) {
			for (var i in data.fields) {
				if (data.fields[i] && data.fields[i].toLowerCase() == field.toLowerCase()) {
					control.val(data.rows[0][i]);
					control.trigger("change");
					break;
				}
			}
		} else {
			if (data.rows[0][0] != null && data.rows[0][0] !== undefined) {
				control.val(data.rows[0][0]);
				control.trigger("change");
			} else {
				control.val("");
				control.trigger("change");
			}
		}
	} else {
		control.val("");
		control.trigger("change");
	}
} else {
	control.val("");
	control.trigger("change");
}
